function getUnstable(ver) {
  ver = ver.replace('-pre');
  var spl = ver.split('.');
  spl = spl.map(function(i) {
    return parseInt(i, 10);
  });
  spl[1]++;
  spl[2] = 'x';
  return spl.join('.');
}

